import React,
      { Component } from 'react';
import { Link } from 'react-router-dom';

class TitleAndTextHorizontal extends Component {

  constructor( props )
  {

    super(props);

  }

  render() {

    var { title, description, avatar } = this.props;

    return (
      <div>

        <div data-description-text="big-title-50" className="container-page hidden">
          <p data-title dangerouslySetInnerHTML={{ __html: title }} />
          <p data-text>
            { description }

            <div data-avatar>
              <i></i>
              { avatar.name }
            </div>

          </p>
        </div>

      </div>
    );
  }
}
export default TitleAndTextHorizontal;
