import React,
      { Component } from 'react';
import { Link } from 'react-router-dom';

import Header                   from './../../components/Header';
import Footer                   from './../../components/Footer';
import CoverPage                from './../../components/CoverPage/TitleText';
import TitleAndTextHorizontal   from './../../components/Contents/TitleAndTextHorizontal';
import TextWithImageHorizontal  from './../../components/Contents/TextWithImageHorizontal';
import CallToActionPharse       from './../../components/Contents/CallToActionPharse';
import { getLanguage }          from './../../components/i18n';

class About extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('pt-br');

    this.state = {
      texts: texts
    }

  }

  processPillarsBlocks( blocks )
  {

    return blocks.map( ( block ) => {

      return (
        <div data-1>

          <div data-pillar-title>

            <i></i>
            <p>{block.title}</p>

          </div>
          <p data-pillar-description>{block.description}</p>

        </div>
      );

    })

  }

  processFloatTexts( arr_texts_floated )
  {

    return arr_texts_floated.map( ( text ) => {

      return <TextWithImageHorizontal
        float={text.float}
        label={text.label}
        title={text.title}
        description={text.description} />

    });

  }

  render() {

    let { texts }  = this.state;
    let page_texts = texts.page_about;

    console.log( page_texts)

    return (
      <div>

      <Header
        menu_texts      = {texts.header}
        languages_texts = {texts.all_languages}
        actual_language = {texts.language}        />

        <CoverPage
          title={ page_texts.cover.title}
          typing_arr_descriptions={page_texts.cover.typing_pharses} />

        <div className="bg-colored">

          <TitleAndTextHorizontal
            title={ ( <p>Nossa <b>Historia</b></p> )  }
            description={ page_texts.introduction.description }
            avatar={{
              image: "imagem_que_vou_adicionar",
              name: "Felipe Oliveira",
              link: "http://www.www.com"
            }} />

          <div data-separate-simple-border></div>

          <div data-description-text="big-title-50" className="container-page hidden">
          <div data-container-pillars>
            <p data-description>{page_texts.introduction.description_pillar}</p>

            <div data-grid-pilares>
              {this.processPillarsBlocks( page_texts.introduction.block_pillars )}
            </div>
          </div>

          </div>

        </div>

        {this.processFloatTexts( page_texts.float_text )}

        <CallToActionPharse
          text    = {page_texts.call_to_action.text}
          button = {page_texts.call_to_action.button} />

        <Footer
          footer_texts = {texts.footer} />

      </div>
    );
  }
}
export default About;
