import React,
       { Component, Fragment }            from 'react';
import { Link }                 from 'react-router-dom';
import Footer                   from './../../components/Footer';
import Languages                from './../../components/Languages';
import FormRoterizador          from './../../components/FormRoterizador';
import PreviewRoterizador       from './../../components/PreviewRoterizador';
import { getLanguage }          from './../../components/i18n';
import PerfectScrollbar         from 'react-perfect-scrollbar';
import { withScriptjs }         from 'react-google-maps';

import { loadDirections } from './../../actions/mapAction';
import Map from './../../components/Map';

import BlockApresentation from './components/BlockApresentation';
import TabHistory         from './components/TabHistory';

import { compose, withProps, lifecycle } from "recompose";

class Roteirizador extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('pt-br');

    this.state = {
      texts: texts,
      arr_route: [],
      directions: null,
    }

    this.updateMap    = this.updateMap.bind(this);

  }

  async updateMap( arr_route )
  {

    let response = await loadDirections( arr_route );

    await this.setState({
      arr_route: arr_route,
      directions: response.success === true ? response.metadata : null
    })

  }

  closeHistoryTab()
  {

  }

  render() {

    let { tab_opened }  = this.state;
    let { routes }  = this.props;

    return (
      <div data-container-history>

        <div data-header>
          <p>Histórico de Rotas</p>
          <button
            className="icon-close"
            onClick={this.closeHistoryTab}></button>
        </div>

        <div>
          <PerfectScrollbar>

            <div data-history-block>
              <p><span>Horario:</span> 12/08/20129 as 12:232 </p>
              <p><span>De:</span> 12/08/20129 as 12:232 </p>
              <p><span>À:</span> 12/08/20129 as 12:232 </p>
              <p><span>N. Paradas:</span> 2</p>
              <button>Visualizar</button>
            </div>

          </PerfectScrollbar>
        </div>

      </div>
    );
  }
}

export default Roteirizador;
