import React,
       { Component }            from 'react';
import { Link }                 from 'react-router-dom';
import Header                   from './../../components/Header';
import Footer                   from './../../components/Footer';
import HomeCover                from './../../components/CoverPage/HomeCover';
import ContainerBlocksVantages  from './../../components/Contents/ContainerBlocksVantages';
import TextWithImageHorizontal  from './../../components/Contents/TextWithImageHorizontal';
import { getLanguage }          from './../../components/i18n';

class Home extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('pt-br');

    this.state = {
      texts: texts
    }

  }

  manageFloatTexts( arr_float_texts )
  {

    return arr_float_texts.map( ( float_text ) => {

      return (
        <TextWithImageHorizontal
          float       = {float_text.float}
          label       = {float_text.label}
          title       = {float_text.title}
          description = {float_text.description}/>
      );

    });

  }

  render() {

    let { texts }  = this.state;
    let page_texts = texts.page_home;

    return (
      <div>

        <Header
          menu_texts      = {texts.header}
          languages_texts = {texts.all_languages}
          actual_language = {texts.language}        />

          <HomeCover
            texts={page_texts.cover}/>

          <ContainerBlocksVantages
            texts={page_texts.intro} />

          {this.manageFloatTexts( page_texts.float_text )}

          <div data-container-personal-depoiment className="bg-colored">

            <div className="container-page">

              <div data-card-personal-depoiment>
                <div data-header-card>
                  <i data-avatar>
                    <i></i>
                  </i>
                  <p data-name>{ page_texts.card_profile.name }<span> { page_texts.card_profile.plus_name }</span></p>
                </div>
                <div data-description>
                  { page_texts.card_profile.description }
                </div>
              </div>

              <div data-text>
                { page_texts.call_to_action.text }
              </div>

              <div data-buttons>

                <Link to={page_texts.call_to_action.buttons[0].link}>
                  <button data-colored="big">{ page_texts.call_to_action.buttons[0].text }</button>
                </Link>

                <Link to={page_texts.call_to_action.buttons[1].link}>
                  <button data-glass="big">{ page_texts.call_to_action.buttons[1].text }</button>
                </Link>

              </div>

            </div>

          </div>

        <Footer
          footer_texts = {texts.footer} />

      </div>
    );
  }
}

export default Home;
