import React,
      { Component } from 'react';
import { Link } from 'react-router-dom';

import Header                   from './../../components/Header';
import Footer                   from './../../components/Footer';
import CoverPage                from './../../components/CoverPage/CenterTitle';
import RisksAndVantages         from './../../components/Contents/RisksAndVantages';
import IntroHow                 from './../../components/Contents/IntroHow';
import TextWithImageHorizontal  from './../../components/Contents/TextWithImageHorizontal';
import CallToActionPharse       from './../../components/Contents/CallToActionPharse';
import { getLanguage }          from './../../components/i18n';

class Services extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('pt-br');

    this.state = {
      texts: texts
    }

  }

  proccessFloat( arr_texts )
  {

    return arr_texts.map( ( text ) => {
      return <TextWithImageHorizontal
              title       = {text.title}
              description = {text.description}
              label       = {text.label}
              image       = {text.image}
              float       = {text.float} />
    });

  }

  render() {

    let { texts }  = this.state;
    let page_texts = texts.page_services;

    return (
      <div>

        <Header
          menu_texts      = {texts.header}
          languages_texts = {texts.all_languages}
          actual_language = {texts.language}        />

        <CoverPage
          title        =  {page_texts.cover.title}
          description  =  {page_texts.cover.description}
          buttons      =  {page_texts.cover.buttons}/>

        <IntroHow
          label       = { page_texts.monetization_mode.label }
          title       = { page_texts.monetization_mode.title }
          description = { page_texts.monetization_mode.description }
          why_us      = { page_texts.why_us } />

        { this.proccessFloat( page_texts.float_text ) }

        <CallToActionPharse
          text    = {page_texts.call_to_action.text}
          button = {page_texts.call_to_action.button} />

        <Footer
          footer_texts = {texts.footer} />

      </div>
    );
  }
}
export default Services;
