import React,
      { Component } from 'react';
import { Link } from 'react-router-dom'

import Languages from './Languages';

class Header extends Component {

  constructor( props )
  {

    super();

  }

  processMenu( menu_texts )
  {

    return menu_texts.map( ( item ) => {
       return (<li><Link to={item.link}>{item.name}</Link></li>);
     });

  }

  render() {

    let { menu_texts      ,
          languages_texts ,
          actual_language } = this.props;

    return (
      <div data-component="header">

        <div data-track-header>

          <div className="container-page hidden">

            <div data-logo>
              <i>
                <i className="icon-coins"></i>
                <p>
                  Oliveira
                  <span>Capital</span>
                </p>
              </i>
            </div>

            <Languages />

            <Link to={menu_texts.buttons.login.link}>
              <button data-glass="small" className="f-right m5">{menu_texts.buttons.login.name}</button>
            </Link>

          </div>

        </div>

        <div data-menu>

          <div className="container-page hidden">

            <nav>
              <ul>

                {this.processMenu( menu_texts.menu )}

                {/*
                <li><Link to="/">Home</Link></li>
                <li><Link to="/about">Sobre</Link></li>
                <li><Link to="/services">Servicos</Link></li>
                <li><Link to="/carrers">Carreiras</Link></li>
                <li><Link to="/contact">Contato</Link></li>
                <li><Link to="/faq">F.A.Q</Link></li>
                */}

              </ul>
            </nav>

            <button data-colored="big" className="f-right m5">Abrir a Minha Conta</button>

          </div>

        </div>
      </div>
    );
  }
}
export default Header;
