import React,
       { Component, Fragment }            from 'react';
import { Link }                 from 'react-router-dom';

import {  register,
          login            } from './../../actions/authAction';

import './../FormLogin/index.css';

class FormRegister extends Component {

  constructor( props )
  {

    super();

    this.state = {
      full_name : "",
      email     : "",
      password  : "",
      loading   : false,
      error     : ""
    }

    this.handleSubmit = this.handleSubmit.bind(this);

  }

  updateState( field, value )
  {

    this.setState({
      [field]: value
    })

  }

  async handleSubmit( e )
  {

    e.preventDefault();

    let { full_name,
          email,
          password } = this.state;

    e.preventDefault();

    this.setState({
      loading: true,
    });

    let response = await register( full_name, email, password);

    if( response.success === true && response.metadata && response.metadata[0] && response.metadata[0]._id ){

      response = await login( email, password);

      if( response.success && response.metadata && response.metadata.authenticated === true ) {
        window.location.reload();
      } else {

        this.setState({
          error: "Erro ao tentar logar, pela pagina de login",
          loading: false,
        })

      }

    } else {

      let error = response.error
                  ? response.error
                  : "Erro ao criar sua conta"

      this.setState({
        error: error,
        loading: false,
      })

    }

  }

  render()
  {

    let self = this;
    let { email,
          password,
          loading,
          error,
          full_name     } = this.state;

    let { texts } = this.props;

    return (
      <div data-component="form-login">

        <form data-form-type={
            loading === true
            ? "loading"
            : ""
          }
          onSubmit={this.handleSubmit}>
          <div data-header>
            <i className="icon-map"></i>
            <h1>Roteirizador</h1>
          </div>

          <div data-field>
            <i className="icon-smiley"></i>
            <input
              type="text"
              value={full_name}
              onChange={ ( e ) => { this.updateState( "full_name", e.target.value ) }}
              placeholder="Digite seu nome completo aqui"   />
          </div>

          <div data-field>
            <i className="icon-mail"></i>
            <input
              type="email"
              value={email}
              onChange={ ( e ) => { this.updateState( "email", e.target.value ) }}
              placeholder="Digite seu e-mail aqui"   />
          </div>

          <div data-field>
            <i className="icon-key"></i>
            <input
              type="password"
              value={password}
              onChange={ ( e ) => { this.updateState( "password", e.target.value ) }}
              placeholder="Digite sua senha aqui"/>
          </div>

          {
            error && error !== ""
            ? (<p data-error>{error}</p>)
            : ""
          }

          <button data-submit>
            Fazer meu Registro
          </button>

          <Link to="/login"><p data-link>Ir Para Login</p></Link>
        </form>

      </div>
    );

  }

}

export default FormRegister;
