import React,
      { Component } from 'react';
import { Link } from 'react-router-dom';

class IntroHow extends Component {

  constructor( props )
  {

    super();

  }

  processBlocksWhy( blocks )
  {

    return blocks.map( ( block ) => {
      return (
        <div data-block-vantage>
          <p data-title>{block.title}</p>
          <p data-description>
            {block.description}
          </p>
        </div>
      );
    });

  }

  processWhySection()
  {

    let { label, title, description, blocks } = this.props.why_us;

    return (
      <div>
      <div data-container-title-how>

        <p data-label>{ label }</p>
        <p data-title>{ title }</p>
        <p data-description>
          { description }
        </p>
        <div data-line-separate-title="true"></div>

      </div>

        <div data-container-blocks-vantages>

        {this.processBlocksWhy( blocks )}

      </div>
      </div>
    );

  }

  render() {

    let { label, title, description, why_us } = this.props;

    return (
    <div className="bg-colored">
      <div className="container-page hidden">

        <div data-how-intro>

          <div data-title-how-intro>
            <p data-label>{ label }</p>
            <p data-title> { title }</p>
            <p data-description>{ description }</p>
          </div>

          <div data-image-candles>
            <div data-candle="1"></div>
            <div data-candle="2"></div>
            <div data-candle="3"></div>
            <div data-candle="4"></div>
            <div data-candle="5"></div>
            <div data-candle="6"></div>
          </div>

        </div>

        <div data-separate-simple-border></div>

        {this.processWhySection()}

      </div>
    </div>
    );
  }
}
export default IntroHow;
