      { Component } from 'react';
import Fields       from './Fields';

import './index.css';

class FormRoterizador extends Component {

  constructor( props )
  {

    super();

    this.state = {
      route: []
    }

  }

  addStop()
  {

  }

  removeStop()
  {

  }

  processFields( route )
  {

    route.map( ( route , key ) => {
      return <Fields />
    });

  }

  render() {

    let { route } = this.state;

    return (
      <form data-component="form-roterizador">

        {this.processFields( route )}
        <div data-field>
          <label>Origem</label>
          <div>
            <i className="icon-home"></i>
            <input type="text" placeholder="Digite a origem aqui"/>
          </div>
        </div>


        <div data-field="with-button">
          <label>Destino</label>
          <div>
            <i className="icon-home"></i>
            <input type="text" placeholder="Digite o destino aqui"/>
          </div>
          <button className="icon-home" type="button"></button>
        </div>

        <button data-button-submit>
          <i className="icon-home"></i>
          Gerar Rota
        </button>

      </form>
    );

  }

}
export default FormRoterizador;
