  constructor( props )
  {

    super();

    this.state = {
      route: [
        {
          type: "from",
          metadata: null,
          remove: false,
        },
        {
          type: "to",
          metadata: null,
          remove: false,
        }
      ]
    }

  }

  addStop()
  {
    alert('aaa')
  }

  removeStop()
