import React,
      { Component } from 'react';

import './index.css';

class PreviewRoterizador extends Component {

  constructor( props )
  {

    super();

    this.state = {
      routes: []
    };

  }

  processRoutes()
  {

    let { directions } = this.props;

console.log( "directions")
    console.log( directions)
    console.log( "directions")

    if( directions && directions.routes && directions.routes[0] && directions.routes[0].legs ){

        let arr_route = directions.routes[0].legs;

        return arr_route.map( ( route, i ) => {

          return (
            <div>
              <i></i>
              <div>
                <p><span>De: </span> {route.start_address}</p>
                <p><span>De: </span> {route.end_address}</p>
                <p><span>Distancia: </span> {route.distance.text}</p>
                <p><span>Duração: </span> {route.distance.duration}</p>
              </div>
            </div>
          )

        });

    } else {
      return "";
    }

  }

  componentWillReceiveProps(){}

  render() {

    let { directions } = this.props;

    return (
      <div data-component="preview-roterizador">

        {this.processRoutes()}

      </div>
    );

  }

}
export default PreviewRoterizador;
