import React,
      { Component } from 'react';
import { Link } from 'react-router-dom'

class HomeCover extends Component {

  constructor( props )
  {

    super();

  }

  processTextBlocks( blocks )
  {

    return blocks.map( ( block ) => {

      return (
        <div data-block>
          <i data-icon className="icon-home"></i>
          <p data-title>{ block.title }</p>
          <p data-description dangerouslySetInnerHTML={{ __html: block.description }} />
        </div>
      );

    });

  }

  render() {

    let { texts } = this.props;

    return (
      <div className="container-page hidden">

        <div data-cover-home>

          <div data-text>

            <p data-title       dangerouslySetInnerHTML={{ __html: texts.title }}/>
            <p data-description dangerouslySetInnerHTML={{ __html: texts.description }} />

            <div data-line-separate-title></div>

            <div>

              {
                texts.buttons && texts.buttons[0]
                ? (
                  <Link to={texts.buttons[0].link}>
                    <button data-colored="big">{texts.buttons[0].title}</button>
                  </Link>
                ) : ''
              }

              {
                texts.buttons && texts.buttons[1]
                ? (
                  <Link to={texts.buttons[1].link}>
                    <button data-glass="big">{texts.buttons[1].title}</button>
                  </Link>
                ) : ''
              }

            </div>

          </div>

          <i data-image></i>

          <div data-container-blocks>
            {this.processTextBlocks( texts.blocks )}
          </div>

        </div>

      </div>
    );
  }
}
export default HomeCover;
