import React,
       { Component, Fragment }            from 'react';
import { Link }                 from 'react-router-dom';

import './index.css';

class Formlogin extends Component {

  constructor( props )
  {

    super();

    this.state = {
      email     : "",
      password  : "",
      loading   : false,
      error     : ""
    }

  }

  updateState( field, value )
  {

    this.setState({
      [field]: value
    })

  }

  render()
  {

    let self = this;
    let { email, password, loading } = this.state;

    return (
      <div data-component="form-login">

        <form data-form-type={
          loading === true
          ? "loading"
          : ""
        }>
          <div data-header>
            <i className="icon-map"></i>
            <h1>Roteirizador</h1>
          </div>

          <div data-field>
            <i className="icon-mail"></i>
            <input
              type="email"
              value={email}
              onChange={ ( e ) => { this.updateState( "email", e.target.value ) }}
              placeholder="Digite seu e-mail aqui"   />
          </div>

          <div data-field>
            <i className="icon-key"></i>
            <input
              type="password"
              value={password}
              onChange={ ( e ) => { this.updateState( "password", e.target.value ) }}
              placeholder="Digite sua senha aqui"/>
          </div>

          <p data-error>erro</p>

          <button data-submit>
            Login
          </button>

          <Link to="/register"><p data-link>Criar minha conta</p></Link>
        </form>

      </div>
    );

  }

}

export default Formlogin;
