import React,
      { Component } from 'react';
import { Link } from 'react-router-dom';

class TextWithImageHorizontal extends Component {

  constructor( props )
  {

    super(props);

  }

  render() {

    var { title, description, label, float, image } = this.props;
    let image_url = require( '../../assets/images/' + image );

    return (
      <div data-text-with-image class="container-page hidden">

        <div data-image
          style={{float: float,
                  backgroundImage: `url(${image_url})no-repeat` }}></div>
        <div data-text
          style={{float: float}}>
          <p data-label>{ label }</p>
          <p data-title> { title }</p>
          <p data-description>{ description }</p>
        </div>

      </div>
    );
  }
}
export default TextWithImageHorizontal;
