import React,
       { Component }            from 'react';
import { Link }                 from 'react-router-dom';
import Footer                   from './../../components/Footer';
import Languages                from './../../components/Languages';
import { getLanguage }          from './../../components/i18n';

class Roteirizador extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('pt-br');

    this.state = {
      texts: texts
    }

  }

  render() {

    let { texts }  = this.state;
    let page_texts = texts.page_home;

    return (
      <div>

        <div data-container-form>

          <h1>Roteirizador</h1>

          <Languages
            languages_texts={texts.all_languages}
            actual_language={texts.actual_language}/>

          <form>

            <div>
              <label>Origem</label>
              <div>
                <i className="icon-home"></i>
                <input type="text" placeholder="Digite a origem aqui"/>
              </div>
            </div>


            <div>
              <label>Destino</label>
              <div>
                <i className="icon-home"></i>
                <input type="text" placeholder="Digite o destino aqui"/>
              </div>
              <button className="icon-home"></button>
            </div>

            <button>
              <i className="icon-home"></i>
              Roteirizar
            </button>

          </form>

          <div data-preview-route>
            <p><span>Dist√¢ncia:</span> 2km </p>
            <p><span>Tempo Estimado:</span> 6 minutos </p>
          </div>

        </div>

        <div data-container-maps></div>


        <Footer />

      </div>
    );
  }
}

export default Roteirizador;
