import React,
      { Component } from 'react';
import { Link } from 'react-router-dom';

import Header                   from './../../components/Header';
import Footer                   from './../../components/Footer';
import CoverPage                from './../../components/CoverPage/TitleText';
import FormContact              from './../../components/Contents/FormContact';
import { getLanguage }          from './../../components/i18n';

class Contact extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('de-de');

    this.state = {
      texts: texts
    }

  }

  render() {

    let { texts }  = this.state;
    let page_texts = texts.page_contact;

    return (
      <div>

        <Header
          menu_texts      = {texts.header}
          languages_texts = {texts.all_languages}
          actual_language = {texts.language}        />

        <CoverPage
          title={page_texts.cover.title} />

        <div className="bg-colored">

          <FormContact
            form = { page_texts.form }/>

          <div data-line-or-separate-contact>
            <p>OU</p>
          </div>

          <div data-suggest-faq>
            <p>
              Veja se sua duvida ja nao foi respondida nas <b>perguntas frequentes</b>
            </p>
            <button data-colored="big">Perguntas Frequentes</button>
          </div>

        </div>

        <Footer
          footer_texts = {texts.footer} />

      </div>
    );
  }
}
export default Contact;
