import React,
       { Component, Fragment }            from 'react';
import { Link }                 from 'react-router-dom';

import PerfectScrollbar         from 'react-perfect-scrollbar';

import { compose, withProps, lifecycle } from "recompose";

class TabHistory extends Component {

  constructor( props )
  {

    super();

    this.state = {
      tab_opened: false,
    }

  }


  closeHistoryTab()
  {

    this.setState({
      tab_opened: false
    })

  }

  openHistoryTab()
  {

    let { tab_opened } = this.state;

    if( tab_opened == false ){

      this.setState({
        tab_opened: true
      })

    }

  }

  render() {

    let self = this;
    let { tab_opened }  = this.state;
    let { routes }  = this.props;

    return (
      <div>

        <div  onClick={ () => { self.openHistoryTab() } }
              data-container-history={
              tab_opened === false
                ? "closed"
                : ""
              } >

          <button
            data-button-history
            className="icon-history"></button>

          <div data-header>
            <p>Histórico de Rotas</p>
            <button
              className="icon-close"
              onClick={ () => { self.closeHistoryTab() } }></button>
          </div>

          <div>
            <PerfectScrollbar>

              <div data-history-block>
                <p><span>Horario:</span> 12/08/20129 as 12:232 </p>
                <p><span>De:</span> 12/08/20129 as 12:232 </p>
                <p><span>À:</span> 12/08/20129 as 12:232 </p>
                <p><span>N. Paradas:</span> 2</p>
                <button>Visualizar</button>
              </div>

            </PerfectScrollbar>
          </div>

        </div>
      </div>
    );
  }
}

export default TabHistory;
