import React,
       { Component }            from 'react';
import Footer                   from './../../components/Footer';
import FormLogin                from './../../components/FormLogin';
import { getLanguage }          from './../../components/i18n';
import LoadingScreen            from './../../components/LoadingScreen';
import {  isAuthenticated  }    from './../../actions/authAction';

/**
 *  Tela de Login
 */
class Login extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage();

    this.state = {
      loading_page: true,
      texts       : texts
    }

  }

  async componentDidMount(){

    document.title    = "Roteirizador | Login"

    let auth_response = await isAuthenticated( 'no_authenticated' );

    if( auth_response.metadata && auth_response.metadata.authenticated === false ){

      this.setState({
        loading_page : false
      });

    }

  }

  renderPage = () => {

    let { texts } = this.state;
    let { footer,
          page_login,
          form_error_messages } = texts;

    return (
      <div>

        <FormLogin
          texts          = { page_login  }
          error_messages = { form_error_messages } />

        <Footer
          type="resize-fixed"
          texts={footer}/>

      </div>
    )

  }

  render()
  {

    let { loading_page } = this.state;

    return loading_page
            ? <LoadingScreen />
            : this.renderPage();

  }

}

export default Login;
