import React,
      { Component } from 'react';
import { Link } from 'react-router-dom';

import Header                   from './../../components/Header';
import Footer                   from './../../components/Footer';
import CoverPage                from './../../components/CoverPage/TitleText';
import { getLanguage }          from './../../components/i18n';
import { getLanguageOfTerms }          from './../../components/i18n_terms';

class TermsOfPrivacy extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('de-de');
    let terms_text = getLanguageOfTerms('de-de');


    this.state = {
      texts: texts,
      texts_terms: terms_text,
    }

  }

  render() {

    let { texts, texts_terms }  = this.state;
    let page_texts = texts.page_privacy;
    let page_terms_texts = texts_terms.terms_of_privacy;

    return (
      <div>

        <Header
          menu_texts      = {texts.header}
          languages_texts = {texts.all_languages}
          actual_language = {texts.language}        />

        <div data-container-terms className="bg-colored">

          <div className="container-page hidden">

            <div data-text-terms>

            <p>
              <div data-title>{page_terms_texts.title}</div>
              {page_terms_texts.description}
            </p>

            <p dangerouslySetInnerHTML={{ __html: page_terms_texts.terms }} />

            </div>

          </div>

        </div>

        <Footer
          footer_texts = {texts.footer} />

      </div>
    );
  }
}
export default TermsOfPrivacy;
