import React, { Component } from "react";
import {
  withGoogleMap,
  withScriptjs,
  GoogleMap,
  DirectionsRenderer
} from "react-google-maps";

class Map extends Component {

  constructor( props )
  {

    super();

    let { route } = props

    this.state = {
      directions: null,
      route: route,
    };

  }

  componentDidMount() {

    let { route } = this.state;

    const directionsService = new window.google.maps.DirectionsService();

    var origin, destination = null;
    var waypoints = [];

    let count = 0;
    for( let i in route ){

      if( route[i].metadata && route[i].metadata.lat ){

        if( count === 0 ){
            origin = { lat: route[i].metadata.lat, lng: route[i].metadata.lng }
        } else if( count === 1 ){
            destination = { lat: route[i].metadata.lat, lng: route[i].metadata.lng }
        } else {
            waypoints.push( { location: route[i].metadata.lat + "," + route[i].metadata.lng, stopover: true } );
        }

        count++;

      }

    }

    directionsService.route(
      {
        origin: origin,
        destination: destination,
        waypoints: waypoints,
        optimizeWaypoints: true,
        travelMode: window.google.maps.TravelMode.DRIVING
      },
      (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          console.log( result)
          this.setState({
            directions: result
          });
        } else {
          console.error(`error fetching directions ${result}`);
        }
      }
    );

  }

  render() {

    let GoogleMapExample = withGoogleMap(props => (
      <GoogleMap
        defaultCenter={{ lat: 40.756795, lng: -73.954298 }}
        defaultZoom={13}
      >
        <DirectionsRenderer
          directions={this.state.directions}
        />
      </GoogleMap>
    ));

    return (
      <div>
        <GoogleMapExample
          containerElement={<div data-google-map />}
          mapElement={<div style={{ height: `100%` }} />}
        />
      </div>
    );

  }
}

export default Map;
