import React,
      { Component } from 'react';
import { Link } from 'react-router-dom';

import Header                   from './../../components/Header';
import Footer                   from './../../components/Footer';
import CoverPage                from './../../components/CoverPage/TitleText';
import TitleAndTextHorizontal   from './../../components/Contents/TitleAndTextHorizontal';
import TextWithImageHorizontal  from './../../components/Contents/TextWithImageHorizontal';
import CallToActionPharse       from './../../components/Contents/CallToActionPharse';
import { getLanguage }          from './../../components/i18n';

class About extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('de-de');

    this.state = {
      texts: texts
    }

  }

  render() {

    let { texts }  = this.state;
    let page_texts = texts.page_about;

    console.log( page_texts)

    return (
      <div>

      <Header
        menu_texts      = {texts.header}
        languages_texts = {texts.all_languages}
        actual_language = {texts.language}        />

        <CoverPage
          title={ page_texts.cover.title}
          typing_arr_descriptions={page_texts.cover.pharses} />

        <div className="bg-colored">

          <TitleAndTextHorizontal
            title={ ( <p>Nossa <b>Historia</b></p> )  }
            description="Porque criamos a Oliveira Capital Lorem ipsum dolor sit amet, consectetur adipisicing elit. Qui ea consequuntur, odit veniam mollitia aliquam reiciendis dignissimos, vitae sapiente neque, cum dolorum. Suscipit expedita obcaecati nesciunt error ut quidem autem"
            avatar={{
              image: "imagem_que_vou_adicionar",
              name: "Felipe Oliveira",
              link: "http://www.www.com"
            }} />

          <div data-separate-simple-border></div>

          <div data-description-text="big-title-50" className="container-page hidden">
          <div data-container-pillars>
            <p data-description>Nossos pilares sao simples, sao baseados em transparencia, altos rendimentos, automatizacao e escalabilidade</p>

            <div data-grid-pilares>
              <div data-1>

                <div data-pillar-title>

                  <i></i>
                  <p>Transparencia</p>

                </div>
                <p data-pillar-description>aio asioasj ioasj oasj oiasj oasj doasj dasoj dioasj doasij dasioj dasio</p>

              </div>
              <div data-2>

                <div data-pillar-title>

                  <i></i>
                  <p>Transparencia</p>
                </div>
                <p data-pillar-description>aio asioasj ioasj oasj oiasj oasj doasj dasoj dioasj doasij dasioj dasio</p>

              </div>

              <div data-3>

                <div data-pillar-title>

                  <i></i>
                  <p>Transparencia</p>

                </div>
                <p data-pillar-description>aio asioasj ioasj oasj oiasj oasj doasj dasoj dioasj doasij dasioj dasio</p>

              </div>

              <div data-4>

                <div data-pillar-title>

                  <i></i>
                  <p>Transparencia</p>

                </div>
                <p data-pillar-description>aio asioasj ioasj oasj oiasj oasj doasj dasoj dioasj doasij dasioj dasio</p>

              </div>
            </div>
          </div>

          </div>

        </div>

        <TextWithImageHorizontal
          float="left"
          label="Missao"
          title="Missao"
          description="oasidj asidjasiod asiodj asdiojasdiojasoiasjioasj aiosdjasio daiodj asiodjadioasjdioasjdioa asdoiasjd asio asiodjasiodjasiodjasioda oiasjd ioas dioas dioas dioas dioas dioasasiodjasiodjasiodjasioda oiasjd ioas dioas dioas dioas dioas dioasasiodjasiodjasiodjasioda oiasjd ioas dioas dioas dioas dioas"/>

        <TextWithImageHorizontal
          float="right"
          label="Visao"
          title="Visao"
          description="oasidj asidjasiod asiodj asdiojasdiojasoiasjioasj aiosdjasio daiodj asiodjadioasjdioasjdioa asdoiasjd asio asiodjasiodjasiodjasioda oiasjd ioas dioas dioas dioas dioas dioasasiodjasiodjasiodjasioda oiasjd ioas dioas dioas dioas dioas dioasasiodjasiodjasiodjasioda oiasjd ioas dioas dioas dioas dioas"/>

        <CallToActionPharse />

        <Footer
          footer_texts = {texts.footer} />

      </div>
    );
  }
}
export default About;
