import React,
      { Component } from 'react';

class Fields extends Component {

  constructor( props )
  {

    super();

    this.state = {}

  }

  processLabel()
  {

    let { type  } = this.props;

    return type === "from"
            ? ( <label>Origem</label> )
            : ( <label>Destino</label> ) ;

  }

  processField()
  {

    let {
      index,
      type,
      metadata,
      remove,
      funcButton
    } = this.props;
console.log( index + " <<<")
    return type === "to"
            ? (
              <span>
                <div>
                  <i className="icon-home"></i>
                  <input type="text" placeholder="Digite o destino aqui"/>
                </div>

                {
                  remove
                  ? ( <button className="icon-close"  type="button" onClick={ () => { funcButton( index ) }} ></button> )
                  : ( <button className="icon-add"    type="button" onClick={ () => { funcButton( this ) }} ></button> )
                }

              </span>
            )
            : (
              <div>
                <i className="icon-home"></i>
                <input type="text" placeholder="Digite a origem aqui"/>
              </div>
            )

  }

  //  funcUpdate
  render() {

    let {
      key,
      type,
      metadata,
      remove
    } = this.props;

    return (
        <div data-field={ type === "to" ? "with-button" : ""  }>
          { this.processLabel() }
          { this.processField() }
        </div>
    );

  }

}
export default Fields;
