import React,
       { Component, Fragment }            from 'react';
import { Link }                 from 'react-router-dom';
import Footer                   from './../../components/Footer';
import Languages                from './../../components/Languages';
import FormRoterizador          from './../../components/FormRoterizador';
import PreviewRoterizador       from './../../components/PreviewRoterizador';
import { getLanguage }          from './../../components/i18n';
import PerfectScrollbar         from 'react-perfect-scrollbar';
import { withScriptjs }         from 'react-google-maps';

import Map from './../../components/Map';

import { compose, withProps, lifecycle } from "recompose";

class Roteirizador extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('pt-br');

    this.state = {
      texts: texts,
      route: []
    }

    this.updateMap    = this.updateMap.bind(this);

  }

  async updateMap( route )
  {

    this.setState({
      route: route
    })

  }

  async verifyRoute( route )
  {

    let checked = true;

    if( route.length < 1 ){
      return false;
    }

    for( let i in route ) {

      if( !(route[i].metadata && route[i].metadata.lat) ){
        checked = false;
      }

    }

    return checked;

  }

  render() {

    let { texts,
          route }  = this.state;

    let verification = this.verifyRoute( route );

    let MapLoader = verification
                      ? withScriptjs(<Map
                                    route={route} />)
                      : null

    let page_texts = texts.page_home;
    let self = this;

    return (
      <div>

        <div data-container-form>

          <PerfectScrollbar>

            <h1>Roteirizador</h1>

            <Languages
              languages_texts={texts.all_languages}
              actual_language={texts.language}/>

            <FormRoterizador
              funcUpdateMap={this.updateMap}/>

            <PreviewRoterizador />

          </PerfectScrollbar>

        </div>

        <div data-container-maps>
{
            // verification
            // ? <MapLoader
            //     googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyBadM1YaUkhSp7Kki0F-Dj382ZxW-8VZxw"
            //     loadingElement={<div />} />
            // : ""
}
        </div>

        <Footer />

      </div>
    );
  }
}

export default Roteirizador;
