import React, { Component } from "react";
import {
  withGoogleMap,
  withScriptjs,
  GoogleMap,
  DirectionsRenderer
} from "react-google-maps";

class Map extends Component {

  constructor( props )
  {

    super();

    this.state = {
      directions: null,
      route: this.props,
    };

  }

  componentDidMount() {

    let { route } = this.props;

    const directionsService = new window.google.maps.DirectionsService();

    let origin, destination = null;
    let waypoints = [];

    for( let i in route ){

      if( route[i].metadata && route[i].metadata.lat ){

        switch ( i ) {

          case 0:
            origin = { lat: route[i].metadata.lat, lng: route[i].metadata.lng }
            break;
          case 1:
            destination = { lat: route[i].metadata.lat, lng: route[i].metadata.lng }
            break;
          default:
            waypoints.push({ lat: route[i].metadata.lat, lng: route[i].metadata.lng });

        }

      }

    }

    directionsService.route(
      {
        origin: origin,
        destination: destination,
        waypoints: waypoints,
        optimizeWaypoints: true,
        travelMode: window.google.maps.TravelMode.DRIVING
      },
      (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          this.setState({
            directions: result
          });
        } else {
          console.error(`error fetching directions ${result}`);
        }
      }
    );

  }

  render() {

    let GoogleMapExample = withGoogleMap(props => (
      <GoogleMap
        defaultCenter={{ lat: 40.756795, lng: -73.954298 }}
        defaultZoom={13}
      >
        <DirectionsRenderer
          directions={this.state.directions}
        />
      </GoogleMap>
    ));

    return (
      <div>
        <GoogleMapExample
          containerElement={<div data-google-map />}
          mapElement={<div style={{ height: `100%` }} />}
        />
      </div>
    );

  }
}

export default Map;
