import React,
      { Component } from 'react';
import { Link } from 'react-router-dom';

import Header                   from './../../components/Header';
import Footer                   from './../../components/Footer';
import CoverPage                from './../../components/CoverPage/TitleText';
import ListOpenedPositions      from './../../components/Contents/ListOpenedPositions';
import { getLanguage }          from './../../components/i18n';

class Carrers extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('de-de');

    this.state = {
      texts: texts
    }

  }

  render() {
    return (
      <div>

        <Header />

        <CoverPage
          title={(<span>Estamos procurando por <b>pessoas Criativas e Inteligentes</b><br /> para se juntar ao nosso time.</span>)} />

        <ListOpenedPositions
          positions={[
            {
              sector: "Desenvolvedores",
              positions: [
                {
                  title   : "Desenvolvedor Node Pleno",
                  location: "Remoto",
                  link    : "#"
                },
                {
                  title   : "Desenvolvedor PHP Senior",
                  location: "Remoto",
                  link    : "#"
                }
              ]
            },
            {
              sector: "Vendas",
              positions: [
                {
                  title   : "Vendedor Externo",
                  location: "Remoto",
                  link    : "#"
                }
              ]
            },
            {
              sector: "Marketing",
              positions: [
                {
                  title   : "Marketeiro WEB Pleno",
                  location: "Remoto",
                  link    : "#"
                }
              ]
            }
          ]}/>

          <div className="m50"></div>

        <Footer/>

      </div>
    );
  }
}

export default Carrers;
