import React,
      { Component } from 'react';

import './index.css';

class PreviewRoterizador extends Component {

  constructor( props )
  {

    super();

    // console.log( props)

  }

  loadingDistance() {

console.log( this.props );
    let { arr_route } = this.props;

    if( arr_route && arr_route.length > 0 ){

      const directionsService = new window.google.maps.DirectionsService();

      var origin, destination = null;
      var waypoints = [];

      let count = 0;
      for( let i in arr_route ){

        if( arr_route[i].metadata && arr_route[i].metadata.lat ){

          if( count === 0 ){
              origin = { lat: arr_route[i].metadata.lat, lng: arr_route[i].metadata.lng }
          } else if( count === 1 ){
              destination = { lat: arr_route[i].metadata.lat, lng: arr_route[i].metadata.lng }
          } else {
              waypoints.push( { location: arr_route[i].metadata.lat + "," + arr_route[i].metadata.lng, stopover: true } );
          }

          count++;

        }

      }


      directionsService.route(
        {
          origin: origin,
          destination: destination,
          waypoints: waypoints,
          optimizeWaypoints: true,
          travelMode: window.google.maps.TravelMode.DRIVING
        },
        (result, status) => {
          if (status === window.google.maps.DirectionsStatus.OK) {

            if( result.routes && result.routes[0] && result.routes[0].legs ){
                console.log('preview' )
                console.log( result.routes[0].legs );
                console.log('final' )
            }

            this.setState({
              directions: result
            });
          } else {
            console.error(`error fetching directions ${result}`);
          }
        }
      );

    }

  }

  render() {

    let { arr_route } = this.props;

    return (
      <div data-component="preview-roterizador">

        <div data-preview-route>
          <p><span>Dist√¢ncia:</span> 2km </p>
          <p><span>Tempo Estimado:</span> 6 minutos </p>
        </div>

      </div>
    );

  }

}
export default PreviewRoterizador;
