import React,
       { Component, Fragment }            from 'react';
import { Link }                 from 'react-router-dom';
import Footer                   from './../../components/Footer';
import Languages                from './../../components/Languages';
import FormRoterizador          from './../../components/FormRoterizador';
import PreviewRoterizador       from './../../components/PreviewRoterizador';
import { getLanguage }          from './../../components/i18n';
import PerfectScrollbar         from 'react-perfect-scrollbar';

import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  DirectionsRenderer,
  Marker
} from 'react-google-maps';

import { compose, withProps, lifecycle } from "recompose";

class Roteirizador extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('pt-br');

    this.state = {
      texts: texts
    }

  }

  render() {

    let { texts }  = this.state;
    let page_texts = texts.page_home;

    const MapWithADirectionsRenderer = compose(
  withProps({
    googleMapURL: "https://maps.googleapis.com/maps/api/js?key=AIzaSyC4R6AN7SmujjPUIGKdyao2Kqitzr1kiRg&v=3.exp&libraries=geometry,drawing,places",
    loadingElement: <div style={{ height: `100%` }} />,
    containerElement: <div style={{ height: `400px` }} />,
    mapElement: <div style={{ height: `100%` }} />,
  }),
  withScriptjs,
  withGoogleMap,
  lifecycle({
    componentDidMount() {
      const DirectionsService = new google.maps.DirectionsService();

      DirectionsService.route({
        origin: new google.maps.LatLng(41.8507300, -87.6512600),
        destination: new google.maps.LatLng(41.8525800, -87.6514100),
        travelMode: google.maps.TravelMode.DRIVING,
      }, (result, status) => {
        if (status === google.maps.DirectionsStatus.OK) {
          this.setState({
            directions: result,
          });
        } else {
          console.error(`error fetching directions ${result}`);
        }
      });
    }
  })
)(props =>
  <GoogleMap
    defaultZoom={7}
    defaultCenter={new google.maps.LatLng(41.8507300, -87.6512600)}
  >
    {props.directions && <DirectionsRenderer directions={props.directions} />}
  </GoogleMap>
);

//     const DirectionsService = new google.maps.DirectionsService();
//
//     DirectionsService.route({
//   origin: new google.maps.LatLng(41.8507300, -87.6512600),
//   destination: new google.maps.LatLng(41.8525800, -87.6514100),
//   travelMode: google.maps.TravelMode.DRIVING,
// }, (result, status) => {
//   if (status === google.maps.DirectionsStatus.OK) {
//     this.setState({
//       directions: result,
//     });
//   } else {
//     console.error(`error fetching directions ${result}`);
//   }
// });


    return (
      <div>

        <div data-container-form>

          <PerfectScrollbar>

            <h1>Roteirizador</h1>

            <Languages
              languages_texts={texts.all_languages}
              actual_language={texts.language}/>

            <FormRoterizador />
            <PreviewRoterizador />

          </PerfectScrollbar>

        </div>

        <div data-container-maps>


        <MapWithADirectionsRenderer />

        </div>

        <Footer />

      </div>
    );
  }
}

export default Roteirizador;
