import React,
       { Component }            from 'react';
import { Link }                 from 'react-router-dom';
import Footer                   from './../../components/Footer';
import Languages                from './../../components/Languages';
import FormRoterizador          from './../../components/FormRoterizador';
import PreviewRoterizador       from './../../components/PreviewRoterizador';
import { getLanguage }          from './../../components/i18n';
import PerfectScrollbar         from 'react-perfect-scrollbar';

import {
  withScriptjs,
  withGoogleMap,
  GoogleMap,
  DirectionsRenderer,
} from 'react-google-maps';

class Roteirizador extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage('pt-br');

    this.state = {
      texts: texts
    }

  }

  render() {

    let { texts }  = this.state;
    let page_texts = texts.page_home;

    const myMap = withScriptjs(withGoogleMap((props) => {
    return (      <GoogleMap
          google={this.props.google}
          zoom={8}
          style={{
  width: '100%',
  height: '100%',
}}
          initialCenter={{ lat: 47.444, lng: -122.176}}
        />)
      }));
//     const DirectionsService = new google.maps.DirectionsService();
//
