import axios from 'axios';

export const isAuthenticated = async ( type = "" ) => {

  let auth_token = localStorage.getItem('auth_token');

  if( auth_token && auth_token !== undefined && auth_token !== null && auth_token !== "" ){

    if( type === 'authenticated') {
        window.location.href="/login"
    }

    return {
      success: true,
      metadata: {
        authenticated: false
      }
    }

  }

  let response = await axios.post('http://localhost:8080/auth', {
    params: {
      token: auth_token,
    }
  });

  if( response.data.success && response.data.metadata && response.data.metadata.authenticated === true ){

      // alert( 'auth')
      // localStorage.setItem('auth_token', );

      if( type === 'no_authenticated') {
          window.location.href="/"
      }

      return {
        success: true,
        metadata: {
          authenticated: true
        }
      }

  } else {

    if( type === 'authenticated') {
        window.location.href="/login"
    }

    // Error
    localStorage.removeItem('auth_token');
    return {
      success: true,
      metadata: {
        authenticated: false
      }
    }


  }


}

export const login = ( login, password) => {

  return new Promise( (resolve, reject) => {

    axios.post('http://localhost:8080/auth?login=' + login + '&password='+ password )
         .then( function( response ){

             if( response.data.authenticated ){

                localStorage.setItem('auth_token', response.data.token );

                return resolve({
                  auth: true,
                });

             } else {

                localStorage.removeItem('auth_token');

                return resolve({
                  auth: false,
                  message: 'Login ou senha invalidos',
                });

             }

         });

  });

}
export const loggout = () => {}
export const register = () => {}
