  }

  render() {

    let { texts,
          route }  = this.state;

    let verification = this.verifyRoute( route );

if( route[0].metadata && route[0].metadata.lat ){
    let MapLoader = withScriptjs(<Map
                                    route={route} />)
                                  } else {
                                    let MapLoader = "ssss";
                                  }

    let page_texts = texts.page_home;
    let self = this;

    return (
      <div>

        <div data-container-form>

          <PerfectScrollbar>

            <h1>Roteirizador</h1>

            <Languages
              languages_texts={texts.all_languages}
              actual_language={texts.language}/>

            <FormRoterizador
              funcUpdateMap={this.updateMap}/>

            <PreviewRoterizador />

          </PerfectScrollbar>

        </div>

        <div data-container-maps>

{
   route[0].metadata && route[0].metadata.lat ?
      <MapLoader
          googleMapURL="https://maps.googleapis.com/maps/api/js?key=AIzaSyBadM1YaUkhSp7Kki0F-Dj382ZxW-8VZxw"
          loadingElement={<div />} />
        : "aaa"
  })


}


        </div>

        <Footer />

      </div>
    );
  }
}

export default Roteirizador;
