import React,
       { Component, Fragment }            from 'react';
import { Link }                 from 'react-router-dom';
import Footer                   from './../../components/Footer';
import FormLogin                from './../../components/FormLogin';
import Languages                from './../../components/Languages';
import { getLanguage }          from './../../components/i18n';
import LoadingScreen            from './../../components/LoadingScreen';

import {  isAuthenticated  }    from './../../actions/authAction';

import { compose, withProps, lifecycle } from "recompose";

class Login extends Component {

  constructor( props )
  {

    super();

    let texts = getLanguage();

    this.state = {
      loading_page: true,
      texts: texts
    }

  }

  async componentDidMount(){

    document.title = "Roteirizador | Login"

    let auth_response = await isAuthenticated( 'no_authenticated' );

    if( auth_response.metadata && auth_response.metadata.authenticated === false ){

      this.setState({
        loading_page : false
      });

    }

  }

  renderPage = () => {

    let { texts } = this.state;
    let { footer, page_login } = texts;

    return (
      <div>

        <Languages
          languages_texts={texts.all_languages}
          actual_language={texts.language}/>

        <FormLogin
          texts={page_login}/>

        <Footer
          type="resize-fixed"
          texts={footer}/>

      </div>
    )

  }

  render()
  {

    let { loading_page } = this.state;

    return loading_page
            ? <LoadingScreen />
            : this.renderPage();

  }

}

export default Login;
