import React,
      { Component } from 'react';
import de_de from './../i18n/de_de.js';
import en_us from './../i18n/en_us.js';
import fr_fr from './../i18n/fr_fr.js';
import pt_br from './../i18n/pr_br.js';

export function getLanguage( lang ){

    switch ( lang ) {
      case 'de-de':
        return de_de;
        break;

      case 'en-us':
        return en_us;
        break;

      case 'fr-fr':
        return fr_fr;
        break;

      case 'pt-br':
        return pt_br;
        break;

      default:
      return pt_br;
      break;

    }

}
